mapper.select:
SELECT * FROM agent_relation ar
left join mem_baseinfo mb
on ar.mem_id = mb.id
where ar.`status` = 1

    and ar.mem_id = #{req.memId}


    and ar.agent_id = #{req.memAgentId}


    and ar.account = #{req.account}

ORDER BY ar.create_time desc

SELECT mb.id AS memId, mb.account AS account, mb.mem_level AS memLevel, mb.real_name AS realName, mb.acc_type AS accType, mb.total_deposit AS totalDeposit, mb.total_enchashment AS totalWithdraw, mb.total_bet AS totalBet, mb.create_time AS registerTime, ar.agent_id AS agentId, ar.superior AS superior, ar.team_num AS teamNum FROM agent_relation ar INNER JOIN mem_baseinfo mb ON ar.mem_id = mb.id
and ar.superior = #{req.superior}
ORDER BY ar.create_time DESC
