mapper.select:
SELECT mb.id as memId, mb.account as account, mb.mem_level AS memLevel, mb.balance as balance, ma.superior as superior FROM mem_baseinfo mb LEFT JOIN agent_relation ma ON mb.id = ma.mem_id AND mb.`status` = 0
where mb.account = #{account}
order by mb.create_time desc

SELECT pmr.mem_id AS memId, pmr.amount AS amount, pmr.account as account, pmr.befor_amount AS beforeAmount, pmr.after_amount AS afterAmount, pmr.operate_type AS operateType, pmr.create_user AS createUser, pmr.create_time AS createTime, pmr.remarks AS remarks FROM pay_manual_recharge pmr
and pmr.operate_type = #{operateType}
and pmr.account = #{account}
order by pmr.create_time desc
